          {movies.map((movie) => (
            <SearchResult key={movie.imdbID} movie={movie} />
          ))}

          <h1 className={styles.endMsg} ref={msgRef}>
            ÎßàÏßÄÎßâ ÌéòÏù¥ÏßÄÏûÖÎãàÎã§
