// import { useEffect } from 'react'
import { useRecoilState } from 'recoil'

import { favoriteMovieState } from 'states/movie'

import MovieItem from 'routes/movies/item'
import styles from './favorites.module.scss'

const Favorites = () => {
  const [favoriteMovies] = useRecoilState(favoriteMovieState)

  return (
    <>
      {favoriteMovies.map((movie) => (
        <MovieItem key={movie.imdbID} movie={movie} />
      ))}
      {!favoriteMovies.length && <div className={styles.noResult}>즐겨찾기한 영화가 없습니다.</div>}
    </>
  )
}

export default Favorites
