import { useState } from 'react'
import { useRecoilState, useRecoilValue, useSetRecoilState } from 'recoil'
import { favoriteMovieState, selectedMovieState } from 'states/movie'
import { IMovie } from 'types/movie'

import ModalPortal from '../modal/modalPortal';
import { MovieModal } from '../modal';

import store from 'storejs'

import cx from 'classnames'
import { HeartIcon, CancelIcon } from 'assets/svgs'
import styles from './searchResult.module.scss'

interface MovieItemProps {
  movie: IMovie
}

const SearchResult = ({ movie }: MovieItemProps) => {
  const { Poster, Title, imdbID, Year, Type } = movie
  const setSelectedMovie = useSetRecoilState(selectedMovieState)

  /* const [favoriteMovies, setFavoriteMovies] = useRecoilState(favoriteMovieState)
   */const selectedMovie = useRecoilValue(selectedMovieState)
  const [isClicked, setIsClicked] = useState(false)



  const [, setfavoriteMovies] = useRecoilState(favoriteMovieState);


  const setLocalStorage = (key: string, value: any) => {
    store.set(key, value)
  }

  const getLocalStorage = (key: string) => {
    return store.get(key)
  }
  const favoriteMovieList = getLocalStorage('favoriteMovies') || [];
  const isFavorite = favoriteMovieList.find((item: IMovie) => item.imdbID === imdbID)


  /*  const isFavorite = favoriteMovies.includes(selectedMovie) */
  const handleOpen = () => {
    setIsClicked(true)
    setSelectedMovie(movie)
  }
  const handleClose = () => {
    setIsClicked(false)
  }
  const addMovie = () => {
    const newFavorites = isFavorite ? favoriteMovieList : [...favoriteMovieList, movie];

    setfavoriteMovies(newFavorites);
    setLocalStorage('favoriteMovies', newFavorites);
  };

  const deleteMovie = () => {
    const newFavorites = favoriteMovieList.filter((item) => item.imdbID !== imdbID);

    setfavoriteMovies(newFavorites);
    setLocalStorage('favoriteMovies', newFavorites);
  };

/*   const handleFavorite = () => {
    if (isFavorite()) {
      setFavoriteMovies((prev) => prev.filter((item) => item.imdbID !== selectedMovie.imdbID))
    } else {
      setFavoriteMovies((prev) => [...prev, selectedMovie])
    }
    setIsClicked(false)
  } */

  const handleFavorite = () => {
    addMovie()
    handleClose()
  };

  return (
    <li className={styles.searchResultWrapper}>
      <div className={styles.searchResult}>
        <img src={Poster} alt='Sorry Poster' aria-label='There is no Poster' />
        <dl className={styles.searchResultInfo}>
          <dt>{Title}</dt>
          <dt>
            {Year}, {Type}
          </dt>
          <HeartIcon
            type='button'
            className={cx(styles.favoriteState, { [styles.checkFavorite]: isFavorite })}
            onClick={handleOpen}
          />
        </dl>
        {movieModal && (
        <ModalPortal>
          <MovieModal onClose={handleModalClose} movie={movie} />
        </ModalPortal>
      )}
       {/*  <div className={cx(styles.favoriteMenu, { [styles.openMenu]: isClicked })}>
          {!favoriteMovies.includes(movie) ? (
            <button type='button' className={styles.addFavoriteToggle} onClick={handleFavorite}>
              <HeartIcon type='button' className={styles.favoriteSVG} />
              즐겨찾기 추가
            </button>
          ) : (
            <button
              type='button'
              className={cx(styles.addFavoriteToggle, { [styles.checkFavorite]: isFavorite })}
              onClick={handleFavorite}
            >
              <HeartIcon type='button' className={cx(styles.favoriteSVG, styles.checkFavorite)} />
              즐겨찾기 제거
            </button>
          )}
          <button type='button' onClick={handleClose}>
            <CancelIcon className={styles.closeSVG} /> 취소
          </button>
        </div> */}
      </div>
    </li>
  )
}

export default SearchResult
